import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { insertUserSchema, insertMemorySchema, insertChatMessageSchema, insertCounterSchema, insertWishlistSchema, insertMemoryCommentSchema } from "@shared/schema";
import bcrypt from "bcrypt";
import session from "express-session";
import passport from "passport";
import { Strategy as GoogleStrategy } from "passport-google-oauth20";
import { z } from "zod";

const GOOGLE_CLIENT_ID = process.env.GOOGLE_CLIENT_ID || process.env.VITE_GOOGLE_CLIENT_ID;
const GOOGLE_CLIENT_SECRET = process.env.GOOGLE_CLIENT_SECRET || process.env.VITE_GOOGLE_CLIENT_SECRET;
const SESSION_SECRET = process.env.SESSION_SECRET || (process.env.NODE_ENV === "development" ? "dev-secret-key-change-in-production" : undefined);

if (!SESSION_SECRET) {
  throw new Error("SESSION_SECRET environment variable must be set");
}

// Google OAuth is optional - only set up if credentials are provided
const GOOGLE_OAUTH_ENABLED = !!(GOOGLE_CLIENT_ID && GOOGLE_CLIENT_SECRET);

const loginSchema = z.object({
  emailOrUsername: z.string().min(1),
  password: z.string().min(1),
});

const baseRegisterSchema = z.object({
  email: z.string().email(),
  username: z.string().min(1),
  password: z.string().min(1),
  confirmPassword: z.string(),
  googleId: z.string().optional(),
  role: z.string().default("guest"),
  avatar: z.string().optional(),
  status: z.string().optional(),
  isOnline: z.boolean().optional(),
  lastSeen: z.date().optional(),
  accountId: z.string().optional(),
});

const registerSchema = baseRegisterSchema.refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

const invitationLoginSchema = z.object({
  emailOrUsername: z.string().min(1),
  password: z.string().min(1),
  invitationCode: z.string().min(1),
});

const invitationRegisterSchema = baseRegisterSchema.extend({
  invitationCode: z.string().min(1),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

export async function registerRoutes(app: Express): Promise<Server> {
  app.use(session({
    secret: SESSION_SECRET!,
    resave: false,
    saveUninitialized: false,
    cookie: { secure: false, maxAge: 24 * 60 * 60 * 1000 } // 24 hours
  }));

  app.use(passport.initialize());
  app.use(passport.session());

  // Passport configuration - only setup Google OAuth if credentials are available
  if (GOOGLE_OAUTH_ENABLED) {
    passport.use(new GoogleStrategy({
      clientID: GOOGLE_CLIENT_ID!,
      clientSecret: GOOGLE_CLIENT_SECRET!,
      callbackURL: "/api/auth/google/callback"
    }, async (accessToken, refreshToken, profile, done) => {
      try {
        let user = await storage.getUserByGoogleId(profile.id);

        if (!user) {
          // Create new user
          const account = await storage.createAccount({
            name: `${profile.displayName}'s Account`
          });

          user = await storage.createUser({
            email: profile.emails![0].value,
            username: profile.displayName || `user_${Date.now()}`,
            googleId: profile.id,
            role: "main_admin",
            accountId: account.id,
            avatar: profile.photos?.[0]?.value,
          });

          await storage.generateInvitationCode(account.id, user.id);
        }

        return done(null, user);
      } catch (error) {
        return done(error);
      }
    }));
  }

  passport.serializeUser((user: any, done) => {
    done(null, user.id);
  });

  passport.deserializeUser(async (id: string, done) => {
    try {
      const user = await storage.getUser(id);
      done(null, user);
    } catch (error) {
      done(error);
    }
  });

  // Auth routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { emailOrUsername, password } = loginSchema.parse(req.body);

      let user = await storage.getUserByEmail(emailOrUsername);
      if (!user) {
        user = await storage.getUserByUsername(emailOrUsername);
      }

      if (!user || !user.password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValid = await bcrypt.compare(password, user.password);
      if (!isValid) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.login(user, (err) => {
        if (err) return res.status(500).json({ message: "Login error" });
        res.json({ user });
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid input" });
    }
  });

  app.post("/api/auth/register", async (req, res) => {
    try {
      const { email, username, password } = registerSchema.parse(req.body);

      const existingUser = await storage.getUserByEmail(email) || await storage.getUserByUsername(username);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const hashedPassword = await bcrypt.hash(password, 10);

      const account = await storage.createAccount({
        name: `${username}'s Account`
      });

      const user = await storage.createUser({
        email,
        username,
        password: hashedPassword,
        role: "main_admin",
        accountId: account.id,
      });

      await storage.generateInvitationCode(account.id, user.id);

      req.login(user, (err) => {
        if (err) return res.status(500).json({ message: "Login error" });
        res.json({ user });
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid input" });
    }
  });

  app.post("/api/auth/invitation-login", async (req, res) => {
    try {
      const { emailOrUsername, password, invitationCode } = invitationLoginSchema.parse(req.body);

      const invitation = await storage.getInvitationByCode(invitationCode);
      if (!invitation || invitation.isUsed) {
        return res.status(400).json({ message: "Invalid invitation code" });
      }

      let user = await storage.getUserByEmail(emailOrUsername);
      if (!user) {
        user = await storage.getUserByUsername(emailOrUsername);
      }

      if (!user || !user.password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValid = await bcrypt.compare(password, user.password);
      if (!isValid) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      await storage.updateUser(user.id, { 
        accountId: invitation.accountId,
        role: "co_admin"
      });
      await storage.useInvitation(invitationCode, user.id);

      req.login(user, (err) => {
        if (err) return res.status(500).json({ message: "Login error" });
        res.json({ user });
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid input" });
    }
  });

  app.post("/api/auth/invitation-register", async (req, res) => {
    try {
      const { email, username, password, invitationCode } = invitationRegisterSchema.parse(req.body);

      const invitation = await storage.getInvitationByCode(invitationCode);
      if (!invitation || invitation.isUsed) {
        return res.status(400).json({ message: "Invalid invitation code" });
      }

      const existingUser = await storage.getUserByEmail(email) || await storage.getUserByUsername(username);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const hashedPassword = await bcrypt.hash(password, 10);

      const user = await storage.createUser({
        email,
        username,
        password: hashedPassword,
        role: "co_admin",
        accountId: invitation.accountId,
      });

      await storage.useInvitation(invitationCode, user.id);

      req.login(user, (err) => {
        if (err) return res.status(500).json({ message: "Login error" });
        res.json({ user });
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid input" });
    }
  });

  app.get("/api/auth/google", passport.authenticate("google", {
    scope: ["profile", "email"]
  }));

  app.get("/api/auth/google/callback", 
    passport.authenticate("google", { failureRedirect: "/" }),
    (req, res) => {
      res.redirect("/dashboard");
    }
  );

  app.post("/api/auth/logout", (req, res) => {
    req.logout((err) => {
      if (err) return res.status(500).json({ message: "Logout error" });
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/me", (req, res) => {
    if (req.isAuthenticated()) {
      res.json({ user: req.user });
    } else {
      res.status(401).json({ message: "Not authenticated" });
    }
  });

  // Middleware to check authentication
  const requireAuth = (req: any, res: any, next: any) => {
    if (req.isAuthenticated()) {
      next();
    } else {
      res.status(401).json({ message: "Authentication required" });
    }
  };

  // Memory routes
  app.get("/api/memories", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const { limit = 20, offset = 0 } = req.query;

      const memories = await storage.getMemories(
        user.accountId, 
        user.id, 
        parseInt(limit), 
        parseInt(offset)
      );

      res.json(memories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch memories" });
    }
  });

  app.post("/api/memories", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const memoryData = insertMemorySchema.parse({
        ...req.body,
        authorId: user.id,
        accountId: user.accountId,
      });

      const memory = await storage.createMemory(memoryData);
      res.json(memory);
    } catch (error) {
      res.status(400).json({ message: "Invalid memory data" });
    }
  });

  app.get("/api/memories/search", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const { q } = req.query;

      if (!q) {
        return res.status(400).json({ message: "Query parameter required" });
      }

      const memories = await storage.searchMemories(user.accountId, q);
      res.json(memories);
    } catch (error) {
      res.status(500).json({ message: "Search failed" });
    }
  });

  // Comment routes
  app.get("/api/memories/:id/comments", requireAuth, async (req, res) => {
    try {
      const { id } = req.params;
      const comments = await storage.getMemoryComments(id);
      res.json(comments);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch comments" });
    }
  });

  app.post("/api/memories/:id/comments", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const { id } = req.params;

      const commentData = insertMemoryCommentSchema.parse({
        ...req.body,
        memoryId: id,
        authorId: user.id,
      });

      const comment = await storage.createComment(commentData);
      res.json(comment);
    } catch (error) {
      res.status(400).json({ message: "Invalid comment data" });
    }
  });

  // Chat routes
  app.get("/api/chat/room", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const room = await storage.getChatRoom(user.accountId);
      res.json(room);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch chat room" });
    }
  });

  app.get("/api/chat/messages", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const { limit = 50, offset = 0 } = req.query;

      const room = await storage.getChatRoom(user.accountId);
      if (!room) {
        return res.status(404).json({ message: "Chat room not found" });
      }

      const messages = await storage.getChatMessages(room.id, parseInt(limit), parseInt(offset));
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  // Counter routes
  app.get("/api/counters", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const counters = await storage.getCounters(user.accountId);
      res.json(counters);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch counters" });
    }
  });

  app.post("/api/counters", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const counterData = insertCounterSchema.parse({
        ...req.body,
        accountId: user.accountId,
      });

      const counter = await storage.createCounter(counterData);
      res.json(counter);
    } catch (error) {
      res.status(400).json({ message: "Invalid counter data" });
    }
  });

  // Wishlist routes
  app.get("/api/wishlist", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const wishlist = await storage.getWishlist(user.id);
      res.json(wishlist);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wishlist" });
    }
  });

  app.post("/api/wishlist", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      const itemData = insertWishlistSchema.parse({
        ...req.body,
        userId: user.id,
      });

      const item = await storage.createWishlistItem(itemData);
      res.json(item);
    } catch (error) {
      res.status(400).json({ message: "Invalid wishlist item data" });
    }
  });

  // Account routes
  app.get("/api/account/invitation-code", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      if (user.role !== "main_admin") {
        return res.status(403).json({ message: "Only main admin can access invitation codes" });
      }

      const account = await storage.getAccount(user.accountId);
      if (!account?.invitationCode) {
        const newCode = await storage.generateInvitationCode(user.accountId, user.id);
        return res.json({ code: newCode });
      }

      res.json({ code: account.invitationCode });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch invitation code" });
    }
  });

  app.post("/api/account/generate-invitation", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      if (user.role !== "main_admin") {
        return res.status(403).json({ message: "Only main admin can generate invitation codes" });
      }

      const code = await storage.generateInvitationCode(user.accountId, user.id);
      res.json({ code });
    } catch (error) {
      res.status(500).json({ message: "Failed to generate invitation code" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket server for real-time features
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const clients = new Map<string, WebSocket>();

  wss.on('connection', (ws: WebSocket, req) => {
    let userId: string | null = null;

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());

        if (message.type === 'auth') {
          userId = message.userId;
          clients.set(userId, ws);
          await storage.updateUser(userId, { isOnline: true });
        }

        if (message.type === 'chat_message' && userId) {
          const user = await storage.getUser(userId);
          if (!user) return;

          const room = await storage.getChatRoom(user.accountId);
          if (!room) return;

          const chatMessage = await storage.createChatMessage({
            roomId: room.id,
            authorId: userId,
            content: message.content,
            type: message.messageType || 'text',
            isEphemeral: message.isEphemeral || false,
            expiresAt: message.isEphemeral ? new Date(Date.now() + (message.messageType === 'ephemeral_video' ? 2 * 60 * 1000 : 2 * 60 * 1000)) : undefined,
          });

          // Send to all users in the same account
          const accountUsers = await storage.getUsersByAccountId(user.accountId);
          accountUsers.forEach(accountUser => {
            const client = clients.get(accountUser.id);
            if (client && client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({
                type: 'new_message',
                message: chatMessage
              }));
            }
          });
        }

      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', async () => {
      if (userId) {
        clients.delete(userId);
        await storage.updateUser(userId, { 
          isOnline: false, 
          lastSeen: new Date() 
        });
      }
    });
  });

  // Clean up expired messages every minute
  setInterval(async () => {
    try {
      await storage.deleteExpiredMessages();
    } catch (error) {
      console.error('Failed to clean up expired messages:', error);
    }
  }, 60000);

  return httpServer;
}

server/storage.ts.content:
import { drizzle } from "drizzle-orm/postgres-js";
import postgres from "postgres";
import {
  users, accounts, memories, memoryComments, chatRooms, chatMessages,
  counters, wishlists, invitations, games,
  type User, type InsertUser, type Account, type InsertAccount,
  type Memory, type InsertMemory, type MemoryComment, type InsertMemoryComment,
  type ChatRoom, type InsertChatRoom, type ChatMessage, type InsertChatMessage,
  type Counter, type InsertCounter, type Wishlist, type InsertWishlist,
  type Invitation, type InsertInvitation, type Game, type InsertGame
} from "@shared/schema";
import { eq, desc, and, or, ilike, gte, lte, isNull } from "drizzle-orm";
import { randomUUID } from "crypto";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL must be set");
}

const client = postgres(process.env.DATABASE_URL);
const db = drizzle(client);

export interface IStorage {
  // User management
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByGoogleId(googleId: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User>;
  getUsersByAccountId(accountId: string): Promise<User[]>;

  // Account management
  getAccount(id: string): Promise<Account | undefined>;
  createAccount(account: InsertAccount): Promise<Account>;
  updateAccount(id: string, updates: Partial<InsertAccount>): Promise<Account>;
  generateInvitationCode(accountId: string, createdById: string): Promise<string>;
  getInvitationByCode(code: string): Promise<Invitation | undefined>;
  useInvitation(code: string, userId: string): Promise<void>;

  // Memory management
  getMemories(accountId: string, userId?: string, limit?: number, offset?: number): Promise<Memory[]>;
  getMemory(id: string): Promise<Memory | undefined>;
  createMemory(memory: InsertMemory): Promise<Memory>;
  updateMemory(id: string, updates: Partial<InsertMemory>): Promise<Memory>;
  deleteMemory(id: string): Promise<void>;
  searchMemories(accountId: string, query: string): Promise<Memory[]>;

  // Comments
  getMemoryComments(memoryId: string): Promise<MemoryComment[]>;
  createComment(comment: InsertMemoryComment): Promise<MemoryComment>;
  deleteComment(id: string): Promise<void>;

  // Chat management
  getChatRoom(accountId: string): Promise<ChatRoom | undefined>;
  createChatRoom(chatRoom: InsertChatRoom): Promise<ChatRoom>;
  updateChatRoom(id: string, updates: Partial<InsertChatRoom>): Promise<ChatRoom>;
  getChatMessages(roomId: string, limit?: number, offset?: number): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  deleteExpiredMessages(): Promise<void>;

  // Counters
  getCounters(accountId: string): Promise<Counter[]>;
  createCounter(counter: InsertCounter): Promise<Counter>;
  updateCounter(id: string, updates: Partial<InsertCounter>): Promise<Counter>;
  deleteCounter(id: string): Promise<void>;

  // Wishlist
  getWishlist(userId: string): Promise<Wishlist[]>;
  createWishlistItem(item: InsertWishlist): Promise<Wishlist>;
  updateWishlistItem(id: string, updates: Partial<InsertWishlist>): Promise<Wishlist>;
  deleteWishlistItem(id: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id));
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.email, email));
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username));
    return result[0];
  }

  async getUserByGoogleId(googleId: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.googleId, googleId));
    return result[0];
  }

  async createUser(user: InsertUser): Promise<User> {
    const result = await db.insert(users).values(user).returning();
    return result[0];
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User> {
    const result = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return result[0];
  }

  async getUsersByAccountId(accountId: string): Promise<User[]> {
    return db.select().from(users).where(eq(users.accountId, accountId));
  }

  async getAccount(id: string): Promise<Account | undefined> {
    const result = await db.select().from(accounts).where(eq(accounts.id, id));
    return result[0];
  }

  async createAccount(account: InsertAccount): Promise<Account> {
    const result = await db.insert(accounts).values(account).returning();
    return result[0];
  }

  async updateAccount(id: string, updates: Partial<InsertAccount>): Promise<Account> {
    const result = await db.update(accounts).set(updates).where(eq(accounts.id, id)).returning();
    return result[0];
  }

  async generateInvitationCode(accountId: string, createdById: string): Promise<string> {
    const code = `${Math.random().toString(36).substr(2, 4).toUpperCase()}-${Math.random().toString(36).substr(2, 4).toUpperCase()}-${Math.random().toString(36).substr(2, 4).toUpperCase()}`;

    await db.insert(invitations).values({
      code,
      accountId,
      createdById,
    });

    await db.update(accounts).set({ invitationCode: code }).where(eq(accounts.id, accountId));

    return code;
  }

  async getInvitationByCode(code: string): Promise<Invitation | undefined> {
    const result = await db.select().from(invitations).where(eq(invitations.code, code));
    return result[0];
  }

  async useInvitation(code: string, userId: string): Promise<void> {
    await db.update(invitations)
      .set({ isUsed: true, usedById: userId, usedAt: new Date() })
      .where(eq(invitations.code, code));
  }

  async getMemories(accountId: string, userId?: string, limit = 20, offset = 0): Promise<Memory[]> {
    let conditions = [eq(memories.accountId, accountId)];

    if (userId) {
      // Filter based on visibility and user permissions
      const user = await this.getUser(userId);
      if (user?.role === 'guest') {
        conditions.push(
          or(
            eq(memories.visibility, 'all'),
            and(
              eq(memories.visibility, 'specific_guests'),
              // Note: Array field searching would need proper array operators in production
            )
          )
        );
      }
    }

    return db.select().from(memories)
      .where(and(...conditions))
      .orderBy(desc(memories.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getMemory(id: string): Promise<Memory | undefined> {
    const result = await db.select().from(memories).where(eq(memories.id, id));
    return result[0];
  }

  async createMemory(memory: InsertMemory): Promise<Memory> {
    const result = await db.insert(memories).values(memory).returning();
    return result[0];
  }

  async updateMemory(id: string, updates: Partial<InsertMemory>): Promise<Memory> {
    const result = await db.update(memories).set(updates).where(eq(memories.id, id)).returning();
    return result[0];
  }

  async deleteMemory(id: string): Promise<void> {
    await db.delete(memories).where(eq(memories.id, id));
  }

  async searchMemories(accountId: string, query: string): Promise<Memory[]> {
    return db.select().from(memories)
      .where(
        and(
          eq(memories.accountId, accountId),
          or(
            ilike(memories.description, `%${query}%`),
            // Note: Array field searching would need proper array operators in production
          )
        )
      )
      .orderBy(desc(memories.createdAt));
  }

  async getMemoryComments(memoryId: string): Promise<MemoryComment[]> {
    return db.select().from(memoryComments)
      .where(eq(memoryComments.memoryId, memoryId))
      .orderBy(desc(memoryComments.createdAt));
  }

  async createComment(comment: InsertMemoryComment): Promise<MemoryComment> {
    const result = await db.insert(memoryComments).values(comment).returning();
    return result[0];
  }

  async deleteComment(id: string): Promise<void> {
    await db.delete(memoryComments).where(eq(memoryComments.id, id));
  }

  async getChatRoom(accountId: string): Promise<ChatRoom | undefined> {
    const result = await db.select().from(chatRooms).where(eq(chatRooms.accountId, accountId));
    return result[0];
  }

  async createChatRoom(chatRoom: InsertChatRoom): Promise<ChatRoom> {
    const result = await db.insert(chatRooms).values(chatRoom).returning();
    return result[0];
  }

  async updateChatRoom(id: string, updates: Partial<InsertChatRoom>): Promise<ChatRoom> {
    const result = await db.update(chatRooms).set(updates).where(eq(chatRooms.id, id)).returning();
    return result[0];
  }

  async getChatMessages(roomId: string, limit = 50, offset = 0): Promise<ChatMessage[]> {
    return db.select().from(chatMessages)
      .where(eq(chatMessages.roomId, roomId))
      .orderBy(desc(chatMessages.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const result = await db.insert(chatMessages).values(message).returning();
    return result[0];
  }

  async deleteExpiredMessages(): Promise<void> {
    await db.delete(chatMessages)
      .where(
        and(
          eq(chatMessages.isEphemeral, true),
          lte(chatMessages.expiresAt || new Date(), new Date())
        )
      );
  }

  async getCounters(accountId: string): Promise<Counter[]> {
    return db.select().from(counters)
      .where(eq(counters.accountId, accountId))
      .orderBy(desc(counters.createdAt));
  }

  async createCounter(counter: InsertCounter): Promise<Counter> {
    const result = await db.insert(counters).values(counter).returning();
    return result[0];
  }

  async updateCounter(id: string, updates: Partial<InsertCounter>): Promise<Counter> {
    const result = await db.update(counters).set(updates).where(eq(counters.id, id)).returning();
    return result[0];
  }

  async deleteCounter(id: string): Promise<void> {
    await db.delete(counters).where(eq(counters.id, id));
  }

  async getWishlist(userId: string): Promise<Wishlist[]> {
    return db.select().from(wishlists)
      .where(eq(wishlists.userId, userId))
      .orderBy(desc(wishlists.priority), desc(wishlists.createdAt));
  }

  async createWishlistItem(item: InsertWishlist): Promise<Wishlist> {
    const result = await db.insert(wishlists).values(item).returning();
    return result[0];
  }

  async updateWishlistItem(id: string, updates: Partial<InsertWishlist>): Promise<Wishlist> {
    const result = await db.update(wishlists).set(updates).where(eq(wishlists.id, id)).returning();
    return result[0];
  }

  async deleteWishlistItem(id: string): Promise<void> {
    await db.delete(wishlists).where(eq(wishlists.id, id));
  }
}

export const storage = new DatabaseStorage();

