  --shadow: 0px 2px 0px 0px hsla(243, 75%, 59%, 0.00), 0px 1px 2px -1px hsla(243, 75%, 59%, 0.00);
  --shadow-md: 0px 2px 0px 0px hsla(243, 75%, 59%, 0.00), 0px 2px 4px -1px hsla(243, 75%, 59%, 0.00);
  --shadow-lg: 0px 2px 0px 0px hsla(243, 75%, 59%, 0.00), 0px 4px 6px -1px hsla(243, 75%, 59%, 0.00);
  --shadow-xl: 0px 2px 0px 0px hsla(243, 75%, 59%, 0.00), 0px 8px 10px -1px hsla(243, 75%, 59%, 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsla(243, 75%, 59%, 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

[data-theme="light"] {
  --background: hsl(0, 0%, 96%);
  --foreground: hsl(0, 0%, 4%);
  --card: hsla(0, 0%, 100%, 0.7);
  --card-foreground: hsl(0, 0%, 4%);
  --popover: hsla(0, 0%, 100%, 0.9);
  --popover-foreground: hsl(0, 0%, 4%);
  --primary: hsl(243, 75%, 59%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsla(0, 0%, 100%, 0.6);
  --secondary-foreground: hsl(0, 0%, 4%);
  --muted: hsla(0, 0%, 100%, 0.4);
  --muted-foreground: hsl(0, 0%, 40%);
  --accent: hsla(0, 0%, 100%, 0.6);
  --accent-foreground: hsl(0, 0%, 4%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsla(0, 0%, 0%, 0.1);
  --input: hsla(0, 0%, 100%, 0.8);
  --ring: hsl(243, 75%, 59%);
  --sidebar: hsla(0, 0%, 100%, 0.7);
  --sidebar-foreground: hsl(0, 0%, 4%);
  --sidebar-border: hsla(0, 0%, 0%, 0.1);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground overflow-x-hidden;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

@layer utilities {
  .glass-morphism {
    background: var(--card);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid var(--border);
  }

  .glass-strong {
    background: var(--popover);
    backdrop-filter: blur(30px);
    -webkit-backdrop-filter: blur(30px);
    border: 1px solid var(--border);
  }

  .tab-active {
    background: linear-gradient(135deg, hsl(243, 75%, 59%) 0%, hsl(199, 89%, 48%) 100%);
    color: white;
  }

  .btn-primary {
    background: linear-gradient(135deg, hsl(243, 75%, 59%) 0%, hsl(199, 89%, 48%) 100%);
    color: white;
    border: none;
  }

  .btn-primary:hover {
    background: linear-gradient(135deg, hsl(243, 75%, 65%) 0%, hsl(199, 89%, 55%) 100%);
    transform: translateY(-1px);
  }

  .online-indicator {
    background: hsl(142, 76%, 36%);
    box-shadow: 0 0 10px hsla(142, 76%, 36%, 0.5);
  }

  .message-ephemeral {
    position: relative;
    overflow: hidden;
  }

  .message-ephemeral::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    animation: shimmer 2s infinite;
  }

  .story-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .story-card:hover {
    transform: translateY(-5px) scale(1.02);
  }

  .memory-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 1.5rem;
    grid-auto-rows: masonry;
  }

  @supports not (grid-template-rows: masonry) {
    .memory-grid {
      display: flex;
      flex-wrap: wrap;
      align-items: flex-start;
    }
  }
}

@keyframes glassMorph {
  0% { opacity: 0; transform: translateY(20px) scale(0.95); }
  100% { opacity: 1; transform: translateY(0) scale(1); }
}

@keyframes pulseSoft {
  0%, 100% { opacity: 0.6; transform: scale(1); }
  50% { opacity: 1; transform: scale(1.05); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes shimmer {
  0% { left: -100%; }
  100% { left: 100%; }
}

.animate-glass-morph {
  animation: glassMorph 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.animate-pulse-soft {
  animation: pulseSoft 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

